import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Control.css';

function Control(){
    const [control, SetControl] = useState(0);

    const handleControl = (e) =>{
        console.log(e.target.id);
    }

    const getControl = async () => {
        var str;

        try {
        SetControl(null);

        const response = await axios.post(
            'http://localhost:7579/Mobius/ireland/sensor/?rcn=3',{
            headers: {
                'Accept': 'application/json',
                'X-M2M-RI': '12345',
                'X-M2M-Origin': 'SOrigin',
                'Content-Type': 'application/json; ty=4'
              }
            }
        );

        SetControl(str);

        }catch (e) {
            // console.log("Control.js error: "+e.getMessae());
        }
    };

    useEffect(() => {
        getControl();
    },[]);

    return(
        <div class="control">

            <div class="slider">
            <div class="slider_blank">
                <div class="slider_title">RED LED</div>
                <div class="switch">
                <div class="switch__1">
                    <input onChange={this.handleControl} id="switch-1" type="checkbox"></input>
                    <label for="switch-1"></label>
                </div>
                </div>
            </div>

            <div class="slider__box">
                <span class="slider_red_btn" id="find1"></span>
                <span class="slider_red_color"></span>
                <span class="slider_red_tooltip">50%</span>
            </div>
            </div>

            <div class="slider">
            <div class="slider_blank">
                <div class="slider_title">BLUE LED</div>
                <div class="switch">
                <div class="switch__2">
                    <input id="switch-2" type="checkbox"></input>
                    <label for="switch-2"></label>
                </div>
                </div>
            </div>

            <div class="slider__box">
                <span class="slider_blue_btn" id="find"></span>
                <span class="slider_blue_color"></span>
                <span class="slider_blue_tooltip">50%</span>
            </div>
            </div>

            <div class="slider">
            <div class="slider_blank">
                <div class="slider_title">FAN</div>
                <div class="switch">
                <div class="switch__3">
                    <input id="switch-3" type="checkbox"></input>
                    <label for="switch-3"></label>
                </div>
                </div>
            </div>


            <div class="slider__box">
                <span class="slider_fan_btn" id="find"></span>
                <span class="slider_fan_color"></span>
                <span class="slider_fan_tooltip">50%</span>
            </div>
            </div>


        </div>
    );

}

export default Control;
