import React from "react";
import axios from 'axios';
import './Alram.css';

function Alram(){
    

    var selectede = document.getElementsByName('radio2');


    const setAlram = e =>{
      var start_hour = document.getElementsByClassName('input_start_hour');
      var start_mimute = document.getElementsByClassName('input_start_minute');
      var end_hour = document.getElementsByClassName('input_end_hour');
      var end_minute =document.getElementsByClassName('input_end_minute');
        for(let i=0;i<selectede.length;i++){
            if(selectede[i].checked === true)
                console.log(selectede[i].value);
                console.log('start_hour: '+ start_hour.name);
                console.log('start_mimute: '+ start_mimute.value);
                console.log('end_hour: '+ end_hour.value);
                console.log('end_minute: '+ end_minute.value);
        }
    }

    return(
        <div class="box">

            <div class="alram_blank"> 
        
                <div class="toggle">
                    <input type="radio" name="radio2" value="1" id="tab-1" checked/>
                    <label for="tab-1" class= "toggle__1">
                    <p>RED</p></label>
                    
                    <input type="radio" name="radio2" value="2" id="tab-2" />
                    <label for="tab-2" class= "toggle__2">
                    <p>BLUE</p></label>
                    
                    <input type="radio" name="radio2" value="3" id="tab-3" />
                    <label for="tab-3" class= "toggle__3">
                    <p>FAN</p></label>
                    
                    <div class="toggle__color"></div>
                </div>

                <div class="set_btn" onClick={setAlram}><p>SET</p></div>
            </div>

          <div class="alram_blank2">
            <div class="time_blank">
              <div class="time_title">Start</div>  
              <div class="form">
                <input type="text" class="input_start_hour" placeholder="Hour"></input>
              </div>
              <div class="form">
                <input type="text" class="input_start_minute" placeholder="Minute"></input>
              </div>

            </div>
            <div class="time_blank">
              <div class="time_title">END</div>
              <div class="form">
                <input type="text" class="input_end_hour" placeholder="Hour"></input>
              </div>
              <div class="form">
                <input type="text" class="input_end_minute" placeholder="Minute"></input>
              </div>
            </div>

          </div>

          
        </div>

    );

}

export default Alram
